$.fn.rndmousetrack=function(e,o,t){switch(console.log(e),e){case"track":if(null===localStorage.getItem(o)){const e=[];this.mousemove(function(o){e.push(`{"x":"${o.pageX-16}","y": "${o.pageY-16}"}`),localStorage.setItem("MouseData",`${e}`)})}else{const e=[localStorage.getItem(o)];this.mousemove(function(o){e.push(`{"x":"${o.pageX-32}","y": "${o.pageY-32}"}`),localStorage.setItem("MouseData",`${e}`)})}break;case"view":const a=localStorage.getItem(o),s=`[${a}]`,l=JSON.parse(s);for(let e=0;e<l.length;e++){const o=l[e];console.log(o),this.append(`<div style="top:${o.y}px;left:${o.x}px;background-color: ${t};width: 32px;height: 32px;position: absolute;opacity: 0.1;border-radius: 100px;z-index: 999999999999;"></div>`)}break;case"clean":localStorage.removeItem(o)}};